import React, { useState, useEffect } from 'react';
import Pages from './pages/Pages';
import backgroundImage from './fondo.jpg';
import pollo from './pollo.jpg';


function App() {
  const [data, setData] = useState({ recipes: [] });

  useEffect(() => {
    fetch("/recipes")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
        console.log(data);
      });
  }, []);

  const backgroundStyles = {
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
  };

  const titleContainerStyles = {
    width: '105%',
    margin: '0 auto',
    paddingTop: '0',
  };

  const titleStyles = {
    backgroundImage: `url(${backgroundImage})`,
    textAlign: 'left',
    fontSize: '2.2rem',
    color: '#006000',
    paddingTop: '80px',
    backgroundSize: '105%',
    backgroundPosition: 'center',
    fontFamily: 'Georgia, serif',
  };

  const recipeTitle = {
  fontSize: '22px',
  color: '#007000',
  marginTop: '10px',
  fontFamily: 'Georgia, serif',
  marginLeft: '10px',
  marginBottom: '5px'
};

const recipeSubtitle = {
  fontSize: '18px',
  fontWeight: 'normal',
  color: '#006800',
  marginTop: '5px',
  marginLeft: '25px',
  fontFamily: 'Georgia, serif',
};

const recipeFont = {
  fontSize: '15px',
  fontWeight: 'normal',
  color: '#000000',
  marginTop: '5px',
  marginLeft: '25px',
  fontFamily: 'Georgia, serif',
};

return (
  <div style={backgroundStyles}>
    <div style={titleContainerStyles}>
      <h1 style={titleStyles}>RecipesApp</h1>
    </div>
    <Pages />
    {typeof data.recipes === 'undefined' ? (
      <p>Loading...</p>
    ) : (
      <div>
        {data.recipes.map((recipe) => (
          <div key={recipe.id} style={{
            border: '0.5px solid #ddd', // Un borde de 1px sÃ³lido con color gris claro
            padding: '5px', // Relleno interno de 20px
            borderRadius: '1px', // Bordes redondeados
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', // Sombra suave
            marginBottom: '10px' // Espacio entre contenedores
          }}>
            <h2 style={recipeTitle}>{recipe.name}</h2>
            <p style={recipeFont}>Posted by: {recipe.post_by}</p>
            <p style={recipeFont}>Valoration: {recipe.valoration}</p>
            <img src={pollo} alt={recipe.name} style={{ maxWidth: '200px', height: 'auto', marginLeft: '30px' }} />
            <h3 style={recipeSubtitle}>Ingredients:</h3>
            <ul>
              {recipe.ingredients.map((ingredient, index) => (
                <li key={index}>{ingredient.ingredient_name}</li>
              ))}
            </ul>
            <h3 style={recipeSubtitle}>Comments:</h3>
            <ul>
              {recipe.comments.map((comment, index) => (
                <li key={index}>
                  <p>Comment: {comment.comment_text}</p>
                  <p>Posted by: {comment.posted_by}</p>
                </li>
              ))}
            </ul>
            <h3 style={recipeSubtitle}>Steps:</h3>
            <ol>
              {recipe.steps.map((step, index) => (
                <li key={index}>{step.step_text}</li>
              ))}
            </ol>
          </div>
        ))}
      </div>
    )}
  </div>
);
              }

export default App;
