import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Pages from './pages/Pages';
import backgroundImage from './fondo.jpg';
import pollo from './pollo.jpg';

function Home() {
  return (
    <div className="menu-container">
      <Link to="/ensaladas" className="menu-item">Ensaladas</Link>
      <Link to="/postres" className="menu-item">Postres</Link>
      <Link to="/populares" className="menu-item">Populares</Link>
      <Link to="/pollos" className="menu-item">Pollos</Link>
    </div>
  );
}

function App() {
  const [data, setData] = useState({ recipes: [] });

  useEffect(() => {
    fetch("/recipes")
      .then((res) => res.json())
      .then((data) => {
        setData(data);
        console.log(data);
      });
  }, []);

  const backgroundStyles = {
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
  };

  const titleContainerStyles = {
    width: '105%',
    margin: '0 auto',
    paddingTop: '0',
  };

  const titleStyles = {
    backgroundImage: `url(${backgroundImage})`,
    textAlign: 'left',
    fontSize: '2.2rem',
    color: '#006000',
    paddingTop: '80px',
    backgroundSize: '105%',
    backgroundPosition: 'center',
    fontFamily: 'Georgia, serif',
  };

  const recipeTitle = {
    fontSize: '22px',
    color: '#007000',
    marginTop: '10px',
    fontFamily: 'Georgia, serif',
    marginLeft: '10px',
    marginBottom: '5px',
  };

  const recipeSubtitle = {
    fontSize: '18px',
    fontWeight: 'normal',
    color: '#006800',
    marginTop: '5px',
    marginLeft: '25px',
    fontFamily: 'Georgia, serif',
  };

  const recipeFont = {
    fontSize: '15px',
    fontWeight: 'normal',
    color: '#000000',
    marginTop: '5px',
    marginLeft: '25px',
    fontFamily: 'Georgia, serif',
  };

  return (
    <Router>
      <div style={backgroundStyles}>
        <div style={titleContainerStyles}>
          <h1 style={titleStyles}>RecipesApp</h1>
        </div>
        <Home />
        {typeof data.recipes === 'undefined' ? (
          <p>Loading...</p>
        ) : (
          <div>
            <Switch>
              <Route path="/ensaladas">
                <Ensaladas />
              </Route>
              <Route path="/postres">
                <Postres />
              </Route>
              <Route path="/populares">
                <Popular />
              </Route>
              <Route path="/pollos">
                <Pollos />
              </Route>
              <Route path="/">
                <Pages />
              </Route>
            </Switch>
            {data.recipes.map((recipe) => (
              <div
                key={recipe.id}
                style={{
                  display: 'inline-block',
                  padding: '10px',
                  borderRadius: '1px',
                  boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
                  marginBottom: '20px',
                  maxWidth: '300px',
                  marginLeft: '30px',
                  marginRight: '30px',
                  marginTop: '30px',
                }}
              >
                <h2 style={recipeTitle}>{recipe.name}</h2>
                <p style={recipeFont}>Posted by: {recipe.post_by}</p>
                <p style={recipeFont}>Valoration: {recipe.valoration}</p>
                <img
                  src={pollo}
                  alt={recipe.name}
                  style={{ maxWidth: '200px', height: 'auto', marginLeft: '30px' }}
                />
                <h3 style={recipeSubtitle}>Ingredients:</h3>
                <ul>
                  {recipe.ingredients.map((ingredient, index) => (
                    <li key={index}>{ingredient.ingredient_name}</li>
                  ))}
                </ul>
                <h3 style={recipeSubtitle}>Comments:</h3>
                <ul>
                  {recipe.comments.map((comment, index) => (
                    <li key={index}>
                      <p>Comment: {comment.comment_text}</p>
                      <p>Posted by: {comment.posted_by}</p>
                    </li>
                  ))}
                </ul>
                <h3 style={recipeSubtitle}>Steps:</h3>
                <ol>
                  {recipe.steps.map((step, index) => (
                    <li key={index}>{step.step_text}</li>
                  ))}
                </ol>
              </div>
            ))}
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
